/* stylelint-disable selector-max-universal, selector-max-type */

@import "./packages/theme/index";

* {
  box-sizing: border-box;
}

html {
  font-family: system-ui, -apple-system;
  font-size: #{map-get($theme-typography, "large")};
}

body {
  margin: 0;
  padding: 0;
}

a {
  color: inherit;
  text-decoration: inherit;
}

.app {
  max-width: $theme-container-width;
  margin: auto;
  padding: 0 $theme-container-padding;
}

.demo {
  display: flex;
  gap: $theme-spacing;
  margin-bottom: $theme-spacing * 2;
}

@import "./packages/button/index";
@import "./packages/input/index";

sources.packages/theme/_variables.scss:
$theme-scope: ".theme-scope" !default;

$theme-font-size: 16px;

$theme-tones: (
  "light": 60,
  "base": 0,
  "dark": 10
);

$theme-colors: (
  "primary": #3f51b5,
  "success": #4caf50,
  "danger": #f44336,
  "warning": #ffc10d
);

$theme-typography: (
  large:  $theme-font-size * 1.125,
  base: $theme-font-size * 1,
  small: $theme-font-size * .875
);

$theme-font-weight: (
  regular: 400,
  medium:  500,
  bold: 700
);

$theme-spacing: 8px;

$theme-container-width: 1440px;
$theme-container-padding: $theme-spacing * 9;

$theme-disabled: 0.3;
$theme-border-radius: 8px;

sources.packages/button/_base.scss:
#{$button-root} {
  --button-border-radius: #{$button-border-radius};

  position: relative;
  display: inline-flex;
  align-items: center;
  justify-content: center;
  height: var(--button-size);
  padding: 0 var(--button-spacing);
  border: none;
  border-radius: var(--button-border-radius);
  background-color: var(--button-bg);
  color: $button-color;
  font-size: var(--button-font-size);
  font-weight: $button-font-weight;
  cursor: pointer;
  user-select: none;
  vertical-align: top;
  touch-action: manipulation;

  &:hover {
    background-color: var(--button-bg-hover);
  }

  &:focus {
    outline: 2px solid transparent;
    box-shadow: #fff 0 0 0 2px, var(--button-focus) 0 0 0 4px;
  }

  &:disabled,
  &.is-disabled {
    opacity: $theme-disabled;
    pointer-events: none;
  }

  &--sm {
    --button-size: #{map-get($button-sizes, "sm")};
    --button-spacing: #{map-get($button-spacings, "sm")};
    --button-font-size: #{map-get($theme-typography, "small")};
  }

  &--md {
    --button-size: #{map-get($button-sizes, "md")};
    --button-spacing: #{map-get($button-spacings, "md")};
    --button-font-size: #{map-get($theme-typography, "base")};
  }

  &--lg {
    --button-size: #{map-get($button-sizes, "lg")};
    --button-spacing: #{map-get($button-spacings, "lg")};
    --button-font-size: #{map-get($theme-typography, "large")};
  }

  &--rounded {
    --button-border-radius: #{$button-border-radius * 4};

    min-width: var(--button-size);
  }

  @each $key, $value in $button-skins {
    &--#{$key} {
      --button-bg: #{get-color($key)};
      --button-bg-hover: #{get-color($key, "dark")};
      --button-focus: #{get-color($key, "light")};
    }
  }
}

