#{$input-root} {
  --input-border-radius: #{$input-border-radius};
  --input-size: #{$input-sizes};
  --input-spacing: #{$input-spacings};
  --input-font-size: #{map-get($theme-typography, "base")};

  position: relative;
  display: inline-flex;
  align-items: center;
  justify-content: center;
  height: var(--input-size);
  padding: 0 var(--input-spacing);
  border: 2px solid var(--input-border);
  border-radius: var(--input-border-radius);
  font-size: var(--input-font-size);
  font-weight: $input-font-weight;
  touch-action: manipulation;

  &:hover {
    border-color: var(--input-border-hover);
  }

  &:focus-within {
    outline: 2px solid transparent;
    box-shadow: #fff 0 0 0 2px, var(--input-focus) 0 0 0 4px;
  }

  &:disabled {
    opacity: $theme-disabled;
    pointer-events: none;
  }

  @each $key, $value in $input-skins {
    &--#{$key} {
      --input-border: #{get-color($key)};
      --input-border-hover: #{get-color($key, "dark")};
      --input-focus: #{get-color($key, "light")};
    }
  }
}
